// Function: if there is a next page; set url accordingly and return, otherwise do nothing.
// Explanation:
// If there is a next page; it's defined in the link response header.
// https://www.w3.org/wiki/LinkHeader
// There may be (and are) multiple links in this header.
// See NGSI-LD Spec Section 6.3.10 Pagination behaviour:
// https://www.etsi.org/deliver/etsi_gs/CIM/001_099/009/01.06.01_60/gs_CIM009v010601p.pdf
// Author: pkel

var outA = null;

const m = msg.headers.link.match(/<([^>]*)>;\s*rel=["']?next["']?/)
if (m) {
    outA = {
        url: env.get('STELLIO_API_URL') + m[1],
        outer_parts: msg.outer_parts,
    };
} else {
    msg.complete = true;
}

